import React, { Component } from 'react';
import { connect } from 'react-redux'

class StepperView extends Component {

  constructor(props) {
    super(props)

    // create the local state of the component
    this.state = {
      counter: 1
    }
  }

  // New way of doing things ES6
  incrementCounter = () => {

    // dispatch an action to set the global state
    this.props.onIncrementDispatch()

    this.setState({
      counter: this.state.counter + 1
    })
    /*
    // update the state
    this.setState({ // setState is asyncronous function
      counter: this.state.counter + 1
    },() => { // fired when the state is set
      // call handleOnIncrement which is passed from the parent (App.js)
      this.props.handleOnIncrement(this.state.counter)
    }) */

  }

  decrementCounter = () => {

    this.props.onDecrementDispatch() 
    /*
    this.setState({
      counter: this.state.counter - 1
    },() => {
        this.props.handleOnDecrement(this.state.counter)
    }) */

  }

  render() {
    return (
    <div>
    {this.props.name}
      <button onClick={this.decrementCounter}>-</button>
      <label>{this.props.passengerCount}</label>
      <button onClick={this.incrementCounter}>+</button>
    </div>

    )
  }

}

const mapStateToProps = (state) => {
  return {
    passengerCount: state.noOfPassengers
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onIncrementDispatch: () => dispatch({type: "INC_PASSENGER"}),
    onDecrementDispatch: () => dispatch({type: "DEC_PASSENGER"})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(StepperView);
