import React, { Component } from 'react';
import axios from 'axios'

const REGISTER_USER_URL = 'http://localhost:5432/register'

export class Register extends Component {

  constructor(props) {
    super(props)

    this.state = {
      user : {}
    }
  }

  registerButtonClick = () => {

    let user = this.state.user

    axios.post(REGISTER_USER_URL,{
      uid: user.uid,
      psw: user.pse

    }).then((response) => {
      console.log(response)
    })

  }

  handleTextBoxChange = (e) => {

      this.setState({
        user : {
          ...this.state.user,
          [e.target.name] : e.target.value
        }
      })

  }

  render() {
    return (
      <div>
          <h1>Register</h1>
        <input type="text" onChange={this.handleTextBoxChange} name="uide" placeholder="User Name" />
        <input type="text" onChange={this.handleTextBoxChange} name="psw" placeholder="Password" />
        <input type="text" onChange={this.handleTextBoxChange} name="first_name" placeholder="First Name" />
        <input type="text" onChange={this.handleTextBoxChange} name="last_name" placeholder="Last Name" />
        <input type="text" onChange={this.handleTextBoxChange} name="email" placeholder="Email" />
        <button onClick={this.registerButtonClick}>Register</button>
      </div>
    )
  }

}
