import React, { Component } from 'react';
import { connect } from 'react-redux'

class VeryLost extends Component {

  constructor(props) {
    super(props)

    // create the local state of the component
    this.state = {
      VeryLost: 0
    }
  }


  incrementCounter = () => {

    // dispatch an action to set the global state
    this.props.onIncrementDispatch()

    this.setState({
      VeryLost: this.state.VeryLost + 1
    })


  }

  decrementCounter = () => {

    this.props.onDecrementDispatch() 


  }

  render() {
    return (
    <div>
      <button onClick={this.incrementCounter}> Very Lost</button>
      <h1>{this.props.VeryLost}</h1>
    </div>

    )
  }

}

const mapStateToProps = (state) => {
  return {
    passengerCount: state.noOfPassengers
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onIncrementDispatch: () => dispatch({type: "INC_PASSENGER"}),
    onDecrementDispatch: () => dispatch({type: "DEC_PASSENGER"})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(VeryLost);
