// GLOBAL STATE 
const initialState = {
  VeryLost: 0,
  sortOfGettingIt: 0,
  gotIt: 0,
  name: 'John'
}


const reducer = (state = initialState, action) => {

  if(action.type == "VERYLOST_COUNTER") {
    return {
      ...state,
      VeryLost: state.VeryLost + 1
    }
  // } else if(action.type == "DEC_COUNTER") {
  //   return {
  //     ...state,
  //     VeryLost: state.VeryLost - 1
    // }
  }

  return state
}


export default reducer
