import React, { Component } from 'react';
import { connect } from 'react-redux'

class SortOfGettingIt extends Component {

  constructor(props) {
    super(props)

    // create the local state of the component
    this.state = {
      SortOfGettingIt: 1
    }
  }


  incrementCounter = () => {

    // dispatch an action to set the global state
    this.props.onIncrementDispatch()

    this.setState({
      SortOfGettingIt: this.state.SortOfGettingIt + 1
    })


  }

  decrementCounter = () => {

    this.props.onDecrementDispatch() 


  }

  render() {
    return (
    <div>
      <button onClick={this.incrementCounter}> Sort Of Getting It!</button>
      <h1>{this.state.SortOfGettingIt}</h1>
    </div>

    )
  }

}

const mapStateToProps = (state) => {
  return {
    SortOfGettingIt: state.SortOfGettingIt
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onIncrementDispatch: () => dispatch({type: "INC_COUNTER"}),
    onDecrementDispatch: () => dispatch({type: "DEC_COUNTER"})
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(SortOfGettingIt);
