import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
// import ClassInput from './components/ClassInput'
// import {ClassChart} from './components/ClassChart'
import VeryLost from './components/VeryLost'
import SortOfGettingIt from './components/SortOfGettingIt'
import GettingIt from './components/GettingIt'
import BarChartComponent from './components/BarChartComponent';
import PieChartComponent from './components/PieChartComponent';
const weekOne = {
  week: 1, veryLost: 12, sortOfGettingIt: 9, gettingIt: 1
}
const weekTwo = {
  week: 2, veryLost: 18, sortOfGettingIt: 10, gettingIt: 2
}

var weeksData = [
  weekOne, weekTwo
]

class App extends Component {
  constructor(props) {
    super(props)
    this.state = { charts: []}

  }

  componentDidMount(){
    this.renderCharts()
  }

  renderCharts = () => {

    var renderedWeeks = []

    var baseColors = [
      'rgba(255,105,145,0.6)',
      'rgba(155,100,210,0.6)',
      'rgba(90,178,255,0.6)'
   ]

    weeksData.forEach((week) => {

      var rendered = <div>


        <BarChartComponent 
          weekNumber={week.week}
          veryLost={week.veryLost}
          sortOfGettingIt={week.sortOfGettingIt}
          gettingIt={week.gettingIt}

        />


      </div>

      renderedWeeks.push(rendered)


    })

    this.setState({charts: renderedWeeks})

  }

  render() {
    return (
      <div>
        <h1>Classroom Emotion</h1>
        <h3>Click The Button That Applies To You...</h3>
        {this.state.charts}
        <VeryLost />
        <SortOfGettingIt />
        <GettingIt />

      </div>
    );
  }

}

export default App;
