// GLOBAL STATE 
const initialState = {
  noOfPassengers: 0,
  name: 'John'
}

const reducer = (state = initialState, action) => {

  if(action.type == "INC_PASSENGER") {
    return {
      ...state,
      noOfPassengers: state.noOfPassengers + 1
    }
  } else if(action.type == "DEC_PASSENGER") {
    return {
      ...state,
      noOfPassengers: state.noOfPassengers - 1
    }
  }

  return state
}

export default reducer
