import React, { Component } from 'react';
// import './ClassInput.css'


class ClassInput extends Component {

  render() {

    let movieItems = this.props.movies.map(function(movie,index){
      return <li key={index}>
      <img className="hero-image" src={`${IMAGES_URL}${movie.hero_image}`} />
      <label>{movie.title}</label>

      </li>
    })

    return (
      <div>
      <h1>{this.props.company}</h1>
      {movieItems}
      </div>
    )
  }

}

export default ClassInput;
