import React, { Component } from 'react';


export class StepperView extends Component {

  constructor(props) {
    super(props)

    // create the local state of the component
    this.state = {
      counter: 5
    }
  }

  // New way of doing things ES6
  incrementCounter = () => {
    // update the state
    this.setState({ // setState is asyncronous function
      counter: this.state.counter + 1
    },() => { // fired when the state is set
      // call handleOnIncrement which is passed from the parent (App.js)
      this.props.handleOnIncrement(this.state.counter)
    })

  }

  decrementCounter = () => {

    this.setState({
      counter: this.state.counter - 1
    },() => {
        this.props.handleOnDecrement(this.state.counter)
    })

  }

  render() {
    return (
    <div>
    {this.props.name}
      <button onClick={this.decrementCounter}>-</button>
      <label>{this.state.counter}</label>
      <button onClick={this.incrementCounter}>+</button>
    </div>

    )
  }

}
